xwikidoc.content:
{{groovy}}
println services.localization.render('office365.documentlist.description')
println ""

def azureAuth = xwiki.parseGroovyFromPage("Office365.Groovy")
azureAuth.setXWiki(xwiki, xcontext, doc)

if (!azureAuth.authenticate()) {
  println """
{{warning}}
${services.localization.render('office365.cannotauthenticate')}
{{/warning}}
"""
} else {
 def escapetool = new org.xwiki.velocity.tools.EscapeTool()
 def text = "xwiki";
 if (request.text)
  text = request.text;
 println """
{{html clean="false"}}
<form action="" method="get">
${services.localization.render('office365.documentlist.searchterm')}: <input type="text" name="text" value="${escapetool.xml(text)}" /> <input type="submit" value="${escapetool.xml(services.localization.render('office365.documentlist.search'))}" />
</form>
{{/html}}
"""

 def driveURL = "https://graph.microsoft.com/v1.0/me/drive/search(q='${escapetool.url(text)}')"
 if (request.debug)
   println "Drive url: ${driveURL}"
 def res = azureAuth.getGraphApiData(driveURL)
 azureAuth.debug("{{html}}{{{ ${res} }}}{{/html}}")

 def counter = 0;
 for (item in res.value) {
   counter++;
   println """* [[${counter}>>||queryString="counter=${counter}"]] - ${item.name} - [[edit>>url:${item.webUrl}]]"""
   if ("${counter}"==request.counter) {
println """
{{html clean=false}}
<iframe src="${item.webUrl.replaceAll("action=default","action=embedview")}" width="100%" height="1000">
</iframe>
{{/html}}
  """
  }
 }
} 

println azureAuth.getAuthentication(request)
println azureAuth.showDebug()

{{/groovy}}
